
public class Boolean_test { // 참과 거짓

	public static void main(String[] args) { //메인메소드 실행
		// TODO Auto-generated method stub
		boolean play = true; //참과 거짓 실행 = 참
		System.out.println("PLAY :" + play); //로그 출력
		
		play = !play; // 실행 !실행
		System.out.println("PLAY :" + play); //로그 출력
		
		play = !play; //실행 !실행
		System.out.println("!!PLAY :" + play); //로그 출력
		
	}

}
