
public class Byte_test { //컴퓨터 기본단위 8비트

	public static void main(String[] args) { //메인 메소드 처음 실행
		// TODO Auto-generated method stub
		byte by1 = -128; // 바이트 = -128
		byte by2 = -30; // 바이트 = -30
		byte by3 = 0; // 바이트 = 0
		byte by4 = 30; // 바이트 = 30
		int by5 = 129; // 정수 = 128
		System.out.println(by1); //로그 출력
		System.out.println(by2); //로그 출력
		System.out.println(by3); //로그 출력
		System.out.println(by4); //로그 출력
		System.out.println(by5); //로그 출력
		
	}

}
