
public class Caculate { //계산하다

	public static void main(String[] args) { //메인 메소드 처음 실행
		// TODO Auto-generated method stub
				int i = 10; // 정수 i = 10
				int j = 20; // 정수 J = 20
				int result = i + j; //정수 결과 = i 10 + j 20
	System.out.println("결과 :"+result); //로그 출력 (결과: result i+j 합한 값)
	System.out.println("양정개발인력센터 수업 테스트 강의"); //로그 출력
	}

}
