
public class Cal_test_1 { //계산하다

	public static void main(String[] args) { // 메인 메소드 처음 실행
		// TODO Auto-generated method stub
		int i = +100; // 정수 i = +100
		int j = -100; // 정수 j = -100
		double k = +3.14; // 소수점 선언 k = +3.14
		double l = -3.14; // 소수점 선언 l = -3.14
		
		int result_1 = -i; // 정수 결과_1 = - (i) =(+100) -> -100
		int result_2 = -j; // 정수 결과_2 = - (j) =(-100) -> 100
		double result_3 = -k; // 소수점 선언 결과_3 = - (k)= (+3.14) -> -3.14
		double result_4 = -l; // 소수점 선언 결과_4 = - (l)= (-3.14) -> 3.14
		
		System.out.println(result_1); //로그 츨력
		System.out.println(result_2); //로그 출력
		System.out.println(result_3); //로그 출력
		System.out.println(result_4); //로그 출력
	}

}
