
public class Float_test { //소수점선언 f

	public static void main(String[] args) { //메인메소드 처음 실행
		// TODO Auto-generated method stub
		//byte, short, int, long 정수타입
		//float, double 실수타입
		
		double var1 = 3.14; //소수점선언 = 3.14
	//	float var2 = 3.14f;
		float var3 = 3.14f; //소수점선언f = 3.14f
		double var4 = 0.1234567890123456789; //소수점선언 = 0.123456789012345678
		float var5 = 0.1234567890123456789f; //소수점선언f = 0.123456789
		
		
		int var6 = 700000; // 정수 = 700000
		float var7 = 7e5f; // 소수점선언f = 소수점 다섯자리
		double var8 = 7e5; // 소수점선언 = 소수점 다섯자리
		double var9 = 7e-3; // 소수점선언 = 소수점 앞에 세자리
		
		
		
		System.out.println("var1: "+ var1); //로그 출력
	//	System.out.println("var2: "+ var2);
		System.out.println("var3: "+ var3); //로그 출력
		System.out.println("var4: "+ var4); //로그 출력
		System.out.println("var5: "+ var5); //로그 출력
		
		System.out.println("var6: "+ var6); //로그 출력
		System.out.println("var7: "+ var7); //로그 출력	
		System.out.println("var8: "+ var8); //로그 출력
		System.out.println("var9: "+ var9);	//로그 출력
		
	}

}
