
public class ParseInt_test { //숫자 함수

	public static void main(String[] args) { //메인 메소드 처음 실행
		// TODO Auto-generated method stub
		int i = Integer.valueOf("1234"); //정수 i= ("1234")
		int j = Integer.parseInt("1234"); //정수 j =("1234")
		int result = i + j; // 정수 = (i=1234) + (j=1234)
		
		System.out.println("결과 :"+ result); // 로그출력("결과 i+j-> result)
		
	}

}
