package test;

public class Substring_test { // 글자 단락 없애는 방법

	public static void main(String[] args) { // 메인 메소드 처음 실행
		// TODO Auto-generated method stub
		
		
		String k = "안녕하세요"; // 문자열 k = 안녕하세요
		
		String j = k.substring(3); // 문자열 j = 앞글자 제거 (3)->3글자
		
		System.out.println("3번째 있는 글자는 : "+j); //로그 출력 
		

	}

}
